import { Component, ViewChild } from '@angular/core';
import { read, utils } from 'xlsx';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  @ViewChild("myInput") formInput!:HTMLInputElement; 

  chooseButtonVisible:boolean = false;
  say(word:string){
    console.log(word)
  }
  theFunction(){
    console.log(this.formInput);
  }
  chosenFunction:string = "";


  fileJSON:any[] = [];
  csvImport($event:any){
    //console.log($event.target.files);
    const files = $event.target.files;
    if(files.length){
      const file = files[0]
      const reader = new FileReader();
      reader.onload = (event:any) => {
        const wb = read(event.target.result);
        const sheets = wb.SheetNames;

        if (sheets.length){
          const rows = utils.sheet_to_json(wb.Sheets[sheets[0]]);
          this.fileJSON = rows;
        }
      };
      reader.readAsArrayBuffer(file)
      console.log(this.fileJSON);
    }
  }


}
<div class = "h-screen bg-gray-950 w-screen">

    <div class= "h-1/2 
    w-1/2 

    flex
    items-center">
<button class = "
w-72 h-20 bg-blue-300

rounded ml-72
"
(click) = "chooseButtonVisible = true">
    Zip Counter
</button>
</div>

<div class= "h-1/2 
absolute
    w-1/2 
    flex
    items-center
    top-0
    right-0"
    >
<button class = "
w-72 h-20 bg-blue-300
rounded m-auto
mr-72
"
(click) = "chosenFunction='converter'">
    Zip Converter
</button>
</div>

<div class = "bg-red-50
w-screen flex items-center">
<input #myInput>
<button
(click) = "theFunction()" 
>Submit Name</button>
</div>

<div 
*ngIf = "chooseButtonVisible"
class = "w-screen bg-red-50 flex items-center">
<input 
type="file" name="file" 
class="custom-file-input
mx-auto
bg-green-300
"
  id="inputGroupFile" required (change)="csvImport ($event)"
  accept=".csv, application/vnd.
  openxmlformats-officedocument. spreadsheetml.sheet,
  application/vnd.ms-excel">
</div>

<app-file-select 
(sendValue)="say($event)"
*ngIf="fileJSON.length"
[fileJSON] = "fileJSON" 
[chosenFunction] = "chosenFunction">

</app-file-select>



</div>
